     // 重置已播放次数
     audio.playCount = 0;
     // 再次播放
     audio.play();
}, audio);
// 加载素材
Resource.load('horse.mp3').then((res: IRes) => {
     // 设置音频素材
     audio.source = res.data;
     // 播放音频
     audio.play();
});
